import React, { useState } from 'react';
import './App.css';

function App() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);

  const handleFileChange1 = (event) => {
    setFile1(event.target.files[0]);
  };

  const handleFileChange2 = (event) => {
    setFile2(event.target.files[0]);
  };

  const handleSubmit = () => {
    if (file1 || file2) {
      console.log("File 1:", file1);
      console.log("File 2:", file2);
      alert("Files submitted successfully!");
    } else {
      alert("Please upload at least one file before submitting.");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="file-upload-container">
          <div className="file-box">
            <label htmlFor="file1" className="file-label">
              {file1 ? file1.name : "Click to Upload"}
            </label>
            <input id="file1" type="file" onChange={handleFileChange1} />
          </div>
          <div className="file-box">
            <label htmlFor="file2" className="file-label">
              {file2 ? file2.name : "Click to Upload"}
            </label>
            <input id="file2" type="file" onChange={handleFileChange2} />
          </div>
        </div>

        {/* Submit Button */}
        <button className="submit-button" onClick={handleSubmit}>
          Submit
        </button>
      </header>
    </div>
  );
}

export default App;
